import './App.css';

import Axios from 'axios'

import React,{useState,useEffect} from 'react';


function App() {

  const [scraps,setScraps]=useState([])

  useEffect(() => {

    fetchScraps();

  }, [])

  useEffect(() => {

    console.log(scraps)

  }, [scraps])

  const fetchScraps=async()=>{

    const response=await Axios('http://localhost:8000/');

    setScraps(response.data)    

  }
  const [value, setValue] = useState('')
  const startscrap = () => {
    Axios.post('http://localhost:8000/', { 'value': value })
      .then(res => console.log(res))
};

  return (

    <div className="App">
       <div>
       <form method="post">
    <select class="form-select" aria-label="Default select link" name='PreviousLink' onchange='if(this.value != 0 and {setValue{this.value}}) { startacrap}'>
         <option value='0'>Link1</option>
         <option value='1'>Link2</option>
         <option value='2'>Link3</option>
         <option value='3'>Link4</option>
    </select>
</form>
             </div>

      {

        scraps && scraps.map(scrap=>{

          return(
           <div>

            <div key={scrap._id} style={{alignItems:'center',margin:'20px 60px'}}>
            <h1>{scrap.pdf_name}</h1>
            <h4><a href={scrap.pdf_link}>{scrap.pdf_link}</a></h4>

            <p>{scrap.company_code}</p>

          </div>
          </div>

          )


        })

      }

    </div>

  );

}


export default App;
