import './App.css';
import Axios from 'axios'
import Header from './components/Header';
import React,{useState,useEffect} from 'react';
import ExpandableList from './components/ExpandableList';
import logo0 from '.logos/logo_0.jpg'

function App() {
  const project_list=[
    "http://investor.weyerhaeuser.com/events-and-presentations",
    "https://investor.fb.com/investor-events/",
    "https://informacioncorporativa.entel.cl/investors/presentations",
    "http://ir.homedepot.com/events-and-presentations"
  ]
  const [scraps,setScraps]=useState([])
  const [one,enable1]=useState(false)
  const [two,enable2]=useState(false)
  const [three,enable3]=useState(false)
  const [four,enable4]=useState(false)
  // const [all,enableAll]=useState(true)

  useEffect(() => {

    fetchScraps();

  }, [])

  useEffect(() => {
    // console.log("all= "+ all);
    console.log(scraps)

  }, [scraps])

  const fetchScraps=async()=>{

    const response=await Axios('http://localhost:8000/');

    setScraps(response.data)    

  }

  return (

    <div className="App">
     <div className="heading"> 
      <Header title="Scrapped Items"/>
      </div>


      <div className="btns"> 
      <img src={logo0}  style={ one ? {backgroundColor: 'red'} :  {backgroundColor: 'pink'}  } onClick={()=>enable1(!one)} alt="Company 1"></img> 
      <img src="logos/logo_1.jpg"   style={ two ? {backgroundColor: 'red'} :  {backgroundColor: 'pink'} } onClick={()=>enable2(!two)} alt="Company 2"></img>
      <img src="logos/logo_2.png"   style={ three ? {backgroundColor: 'red'} :  {backgroundColor: 'pink'} } onClick={()=>enable3(!three)} alt="Company 3"></img>
      <img src="logos/logo_3.png"   style={ four ? {backgroundColor: 'red'} :  {backgroundColor: 'pink'}  } onClick={()=>enable4(!four)} alt="Company 4"></img>
{/* 
      <button className="btnn" type="button" style={ one ? {backgroundColor: 'red'} :  {backgroundColor: 'pink'}  } onClick={()=>enable1(!one)}>Company 1</button> 
      <button className="btnn" type="button" style={ two ? {backgroundColor: 'red'} :  {backgroundColor: 'pink'} } onClick={()=>enable2(!two)}>Company 2</button>
      <button className="btnn" type="button" style={ three ? {backgroundColor: 'red'} :  {backgroundColor: 'pink'} } onClick={()=>enable3(!three)}>Company 3</button> */}
      {/* <button className="btnn" type="button" style={ four ? {backgroundColor: 'red'} :  {backgroundColor: 'pink'}  } onClick={()=>enable4(!four)}>Company 4</button> */}
      </div>
      <div className="show">
      { one && <ExpandableList company_code={1} title={project_list[0]} content={scraps.filter(x=>x.company_code===0)}/>}
      {two && <ExpandableList company_code={2} title={project_list[1]} content={scraps.filter(x=>x.company_code===1)}/>}
      {three && <ExpandableList company_code={3} title={project_list[2]} content={scraps.filter(x=>x.company_code===2)}/>}
      {four && <ExpandableList company_code={4} title={project_list[3]} content={scraps.filter(x=>x.company_code===3)}/>}
      {!one && !two && !three && !four && <h1>Please Select a company</h1> }
      </div>
    </div>

  );

}


export default App;
