import './../App.css';
import React, { useState } from 'react';
import Pdfs from './Pdfs';
const ExpandableList = ({ company_code, title, content }) => {
  const [isActive, setIsActive] = useState(false);
  const company_name=[
    "Weyerhaeuser","Facebook","Informacion Corporativa","The Home Depot"
  ]

  return (
    <div className="accordion-item">
      <div className="accordion-title" style={{color:'black',backgroundColor:'silver'}} onClick={() => setIsActive(!isActive)}>
        <div><h2>{company_code}. {company_name[company_code]}</h2></div>
        <div className="expand" >{isActive ? 'Close' : 'Show'}</div>
      </div>
      {isActive && <div className="accordion-content">{<Pdfs list_pdfs={content}/>}</div>}
    </div>
  );
};

export default ExpandableList;
