import './App.css';
import Axios from 'axios'
import Header from './components/Header';
import React,{useState,useEffect} from 'react';
import ExpandableList from './components/ExpandableList';

function App() {
  const project_list=[
    "http://investor.weyerhaeuser.com/events-and-presentations",
    "https://investor.fb.com/investor-events/",
    "https://informacioncorporativa.entel.cl/investors/presentations",
    "http://ir.homedepot.com/events-and-presentations"
  ]
  let [scraps,setScraps]=useState([])
  const [one,enable1]=useState(false)
  const [two,enable2]=useState(false)
  const [three,enable3]=useState(false)
  const [four,enable4]=useState(false)
  let [sort, setSort] = useState(0);

  useEffect(() => {
    fetchScraps();
  }, []);

  const fetchScraps=async()=>{
    const response=await Axios('http://localhost:8000/');
    setScraps(response.data)    
  }

  const sortScraps = (k) => {
    if (sort === 1) return k.sort((a, b) => a.pdf_date - b.pdf_date);
    else if (sort === 2) return k.sort((a, b) => b.pdf_date - a.pdf_date);
    else if (sort === 3)
      return k.sort((a, b) => a.pdf_name.localeCompare(b.pdf_name));
    else if (sort === 4)
      return k.sort((a, b) => b.pdf_name.localeCompare(a.pdf_name));
    else return k;
  };

  return (

    <div className="App">
     <div className="heading"> 
      <Header title="Scrapped Items"/>
      </div>

      <div className="btns"> 
      <button className="btnn" type="button" style={ one ? {backgroundColor: 'red'} :  {backgroundColor: 'pink'}  } onClick={()=>enable1(!one)}><img class="image_button"src="logos/logo_0.jpg" alt="Company 1"></img> </button> 
      <button className="btnn" type="button" style={ two ? {backgroundColor: 'red'} :  {backgroundColor: 'pink'} } onClick={()=>enable2(!two)}><img class="image_button" src="logos/logo_1.jpg" alt="Company 2"></img></button>
      <button className="btnn" type="button" style={ three ? {backgroundColor: 'red'} :  {backgroundColor: 'pink'} } onClick={()=>enable3(!three)}><img class="image_button" src="logos/logo_2.png" alt="Company 3"></img></button>
      <button className="btnn" type="button" style={ four ? {backgroundColor: 'red'} :  {backgroundColor: 'pink'}  } onClick={()=>enable4(!four)}><img class="image_button" src="logos/logo_3.png" alt="Company 4"></img></button>
      </div>

      <label for="options">Sort By: </label>
      <select name="option" >
        <option style={sort === 1 ? { backgroundColor: "red" }: { backgroundColor: "pink" }} onClick={() => setSort(1)}>Older First</option>
        <option onClick={() => setSort(2)}>Newer First</option>
        <option onClick={() => setSort(3)}>A to Z</option>
        <option onClick={() => setSort(4)}>Z to A</option>
      </select>
      <div className="dropdown">
        <span>Sort By</span>
        <div className="dropdown-content">
          <button type="button" style={sort === 1 ? { backgroundColor: "red" }: { backgroundColor: "pink" }} onClick={() => setSort(1)}>Older First</button>
          <button type="button" style={sort === 2 ? { backgroundColor: "red" }: { backgroundColor: "pink" }} onClick={() => setSort(2)}>Newer First</button>
          <button type="button" style={sort === 3 ? { backgroundColor: "red" }: { backgroundColor: "pink" }} onClick={() => setSort(3)}>A to Z</button>
          <button type="button" style={sort === 4 ? { backgroundColor: "red" }: { backgroundColor: "pink" }} onClick={() => setSort(4)}>Z to A</button>
        </div>
      </div>

      <div className="show">
      { one && <ExpandableList company_code={1} title={project_list[0]} content={sortScraps(scraps.filter((x) => x.company_code === 0))}/>}
      {two && <ExpandableList company_code={2} title={project_list[1]} content={sortScraps(scraps.filter((x) => x.company_code === 1))}/>}
      {three && <ExpandableList company_code={3} title={project_list[2]} content={sortScraps(scraps.filter((x) => x.company_code === 2))}/>}
      {four && <ExpandableList company_code={4} title={project_list[3]} content={sortScraps(scraps.filter((x) => x.company_code === 3))}/>}
      {!one && !two && !three && !four && <h1>Please Select a company</h1> }
      </div>
    </div>

  );

}


export default App;
