import React, { useState } from 'react';
import Pdfs from './Pdfs';
const ExpandableList = ({ title, content }) => {
  const [isActive, setIsActive] = useState(false);
function changeBackgroundOnEnter(e){
  e.target.style.backgroundColor='silver'
  // e.target.style.color='black'
}
function changeBackgroundOnLeave(e){
  e.target.style.backgroundColor='white'
  // e.target.style.color='black'
}
  return (
    <div className="accordion-item">
      <div className="accordion-title" style={{color:'black',backgroundColor:'silver'}} onClick={() => setIsActive(!isActive)}>
        <div>{title}</div>
        <div>{isActive ? '-' : '+'}</div>
      </div>
      {isActive && <div className="accordion-content" >{<Pdfs list_pdfs={content}/>}</div>}
    </div>
  );
};

export default ExpandableList;
